mapper.update:
UPDATE member
SET is_active = 0
WHERE member_id = #{memberId}

UPDATE member


 nickname = #{update.nickname}


        id = #{update.id}


        email = #{update.email}


WHERE member_id = #{memberId}

mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN TRUE ELSE FALSE END
FROM member
WHERE id = #{id}

SELECT CASE WHEN COUNT(1) > 0 THEN TRUE ELSE FALSE END
FROM member
WHERE nickname = #{nickname}

SELECT CASE WHEN COUNT(1) > 0 THEN TRUE ELSE FALSE END
FROM member
WHERE email = #{email}

SELECT is_active
FROM member
WHERE member_id = #{memberId}
